const chalk = require("chalk");

exports.printHeader = function (name, version) {
  console.log(chalk.green("***********************************"));
  console.log(chalk.green("******** Starting Publish *********"));

  console.log(`                           /)`);
  console.log(`                   /\\___/\\ ((`);
  console.log(`                   \\\`@_@'/  ))`);
  console.log(`                   {_:Y:.}_//`);
  console.log(
    `${chalk.green("*******************")}{_}^-'{_}${chalk.green("*******")}`
  );

  console.log(`
Name : ${chalk.blue(`${name}`)}
Current Version : ${chalk.blue(`${version}`)}
`);
};
