import { FC, ReactNode } from "react";
import { Button as RBButton } from "react-bootstrap";

export interface ButtonProps {
  children?: ReactNode;
  type?: "primary" | "secondary";
  onClick?: () => void;
  disabled?: boolean;
}

export const Button: FC<ButtonProps> = ({
  children,
  type,
  onClick,
  disabled,
}: ButtonProps) => {
  let varient = "primary";
  let classes = [];

  switch (type) {
    case "primary":
      classes.push("btn btn-primary");
      break;
    case "secondary":
      classes.push("cora-button-secondary");
      varient = `outline-${varient}`;
      break;
  }

  return (
    <RBButton
      variant={varient}
      className={`cora-button ${classes.join(" ")}`}
      onClick={onClick}
      disabled={disabled}
    >
      <span>{children}</span>
    </RBButton>
  );
};

export default Button;
