import { FC, ReactNode } from "react";
import { Button as RBButton } from "react-bootstrap";

export interface ButtonProps {
  children?: ReactNode;
  /**
   * Displays a numeric notification on the button
   */
  count?: number;
  onClick?: () => void;
  disabled?: boolean;
}

export const ButtonIcon: FC<ButtonProps> = ({
  children,
  count,
  onClick,
  disabled,
}: ButtonProps) => {
  let classes = [];

  if (count && count > 0) classes.push("display-count");

  return (
    <RBButton
      variant="primary"
      data-count={count}
      className={`cora-button-icon ${classes.join(" ")}`}
      onClick={onClick}
      disabled={disabled}
    >
      <span>{children}</span>
    </RBButton>
  );
};

export default ButtonIcon;
