import { FC, ReactNode } from "react"
import { Button as RBButton } from "react-bootstrap"

export interface ButtonProps {
  children?: ReactNode
  onClick?: () => void
  disabled?: boolean
}

export const ButtonLink: FC<ButtonProps> = ({
  children,
  onClick,
  disabled,
}: ButtonProps) => {
  return (
    <RBButton
      variant="primary"
      className="cora-button-link"
      onClick={onClick}
      disabled={disabled}
    >
      <span>{children}</span>
    </RBButton>
  )
}

export default ButtonLink
