import React from "react";
import { Form } from "react-bootstrap";
import styled from "styled-components";
import { IFormField } from "../Common/Interfaces/IFormField";

export interface ICheckRadioProps extends IFormField {
  checked?: boolean;
  onChange?: React.ChangeEventHandler<HTMLInputElement>;
  type?: "checkbox" | "radio";
}

export const CheckRadio: React.FC<ICheckRadioProps> = ({
  id,
  label,
  name,
  checked,
  helpText,
  validation,
  disabled,
  onChange,
  type,
}) => {
  return (
    <div>
      <Form.Check className="form-check" type={type ? type : "checkbox"}>
        <Form.Check.Input
          type={type ? type : "checkbox"}
          isValid={validation?.isValid}
          isInvalid={validation?.isInvalid}
          name={name}
          id={id}
          onChange={onChange}
          disabled={disabled}
          checked={checked}
        />
        <Form.Check.Label>{label}</Form.Check.Label>
        {validation && (
          <Form.Control.Feedback
            type={validation.isInvalid ? "invalid" : "valid"}
          >
            {validation.validationMessage}
          </Form.Control.Feedback>
        )}
      </Form.Check>
      {helpText && <Form.Text className="text-muted">{helpText}</Form.Text>}
    </div>
  );
};

export default CheckRadio;
