import React from "react";
import { Form } from "react-bootstrap";
import CheckRadio, { ICheckRadioProps } from "../CheckRadio";
import { v4 as createUniqueKey } from "uuid";
import { type } from "os";

export interface ICheckboxGroupProps {
  checkboxGroupItems: ICheckRadioProps[];
  inline?: boolean;
  name: string;
  type: "checkbox" | "radio";
}

export const CheckRadioGroup: React.FC<ICheckboxGroupProps> = ({
  checkboxGroupItems,
  name,
  type,
}) => {
  return (
    <div className="mb-3">
      {checkboxGroupItems.map((checkbox) => {
        return (
          <CheckRadio
            key={createUniqueKey()}
            label={checkbox.label}
            helpText={checkbox.helpText}
            validation={checkbox.validation}
            checked={checkbox.checked}
            onChange={checkbox.onChange}
            id={checkbox.id}
            name={name}
            type={type}
          />
        );
      })}
    </div>
  );
};

export default CheckRadioGroup;
