import { XIcon } from "@primer/octicons-react"
import React, { FC, useCallback, useState } from "react"
import { useDropzone } from "react-dropzone"
import { Button, ButtonIcon } from "../.."

export interface FileUploadProps {
  children?: React.ReactNode
  title?: string
}

export const FileUpload: FC<FileUploadProps> = ({
  children,
  title,
}: FileUploadProps) => {
  const onDrop = useCallback((acceptedFiles) => {
    acceptedFiles.forEach((file: any) => {
      const reader = new FileReader()

      reader.onabort = () => console.log("file reading was aborted")
      reader.onerror = () => console.log("file reading has failed")
      reader.onload = () => {
        // Do whatever you want with the file contents
        const binaryStr = reader.result
        console.log(binaryStr)
      }
      reader.readAsArrayBuffer(file)
    })
  }, [])
  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop })
  const [showInfo, setShowInfo] = useState(true)
  const hideInfo = () => setShowInfo(false)

  return (
    <>
      <div
        className="file-upload d-flex flex-column text-center"
        {...getRootProps()}
      >
        <input {...getInputProps()} />
        <div className="drag-n-drop"></div>
        {isDragActive && <p>Drop your file here...</p>}
        {!isDragActive && <p>Drag and drop your CSV file here, or</p>}
        <div className="col-lg-12">
          <Button type="secondary">Browse for File</Button>
        </div>
      </div>

      {children && showInfo && (
        <div className="upload-info">
          <div className="upload-info-body">{children}</div>
          <div className="upload-info-x">
            <ButtonIcon onClick={hideInfo}>
              <XIcon size={28} />
            </ButtonIcon>
          </div>
        </div>
      )}
    </>
  )
}

export default FileUpload
