import React from "react";

interface LinkProps {
  href: string;
  isActive: boolean;
}

const Link: React.FC<LinkProps> = ({ children, href, isActive }) => {
  const classes = ["cora-link", "nav-link"];
  if (isActive) {
    classes.push("nav-link-active");
  }
  return (
    <a className={classes.join(" ")} href={href}>
      {children}
    </a>
  );
};

export default Link;
