import React, { FC, ReactNode } from "react";
import { Form } from "react-bootstrap";

export interface SwitchProps {
  id?: string;
  label: string;
  name?: string;
  checked?: boolean;
  disabled?: boolean;
  onChange?: React.ChangeEventHandler<HTMLInputElement>;
}

export const Switch: FC<SwitchProps> = ({
  id,
  label,
  name,
  checked,
  disabled,
  onChange,
}: SwitchProps) => {
  return (
    <Form.Check
      type="switch"
      id={id}
      label={label}
      name={name}
      checked={checked}
      disabled={disabled}
      onChange={onChange}
    />
  );
};

export default Switch;
