import React from "react";
import { Toast, ToastProps } from "../Toast";
import { ToastContainer } from "react-bootstrap";
import { v4 as createUniqueKey } from "uuid";

export interface ToastGroupProps {
  toasts: ToastProps[];
  position:
    | "top-start"
    | "top-center"
    | "top-end"
    | "middle-start"
    | "middle-center"
    | "middle-end"
    | "bottom-start"
    | "bottom-center"
    | "bottom-end";
}

export const ToastGroup: React.FC<ToastGroupProps> = ({ toasts, position }) => {
  return (
    <ToastContainer className="p-3" position={position}>
      {toasts.map((toast) => {
        return (
          <Toast
            key={createUniqueKey()}
            title={toast.title}
            message={toast.message}
            autoHide={toast.autoHide}
            variant={toast.variant}
            actionText={toast.actionText}
            action={toast.action}
          ></Toast>
        );
      })}
    </ToastContainer>
  );
};

export default ToastGroup;
